query:
ALTER TABLE organizations ADD COLUMN IF NOT EXISTS orgDatabase VARCHAR(255);

UPDATE organizations 
SET orgDatabase = CONCAT('supabase_org_', slug)
WHERE orgDatabase IS NULL OR orgDatabase = '';

ALTER TABLE projects ADD COLUMN IF NOT EXISTS databaseSchema VARCHAR(255);

UPDATE projects p
JOIN organizations o ON p.organizationId = o.id
SET p.databaseSchema = CONCAT('project_', p.slug),
    p.databaseName = o.orgDatabase
WHERE p.databaseSchema IS NULL OR p.databaseSchema = '';

